INSERT INTO pizzas (id, name, description, price, picture) VALUES (1,'Margherita', 'The queen of pizzas, a timeless classic that celebrates simplicity and fresh flavors.', 7.50, 'https://images.unsplash.com/photo-1513104890138-7c749659a591'),(2,'Pepperoni', 'A bold and spicy favorite, perfect for those who love a little heat with every bite.', 8.50, 'https://assets.afcdn.com/recipe/20190319/89655_w3072h2304c1cx3680cy2456.jpg'),(3,'Quattro Stagioni', 'Four seasons in one pizza, representing the best of each with distinct toppings per quarter.', 9.00, 'https://upload.wikimedia.org/wikipedia/commons/thumb/a/a6/Pizza_Quattro_Stagioni.jpg/2560px-Pizza_Quattro_Stagioni.jpg'),(4,'Vegetariana', 'A garden-fresh delight loaded with colorful seasonal vegetables for the health-conscious.', 8.00, 'https://thumbs.dreamstime.com/b/pizza-vegetariana-con-le-verdure-63252208.jpg'),(5,'Diavola', 'Devilishly delicious with fiery salami that brings the heat to your taste buds.', 8.80, 'https://www.cucinare.it/uploads/wp-content/uploads/2015/10/ricetta_pizza_alla_diavola.jpg'),(6,'Capricciosa', 'An indulgent medley of premium toppings that creates a symphony of flavors.', 9.20, 'https://media.gettyimages.com/id/1571646268/it/foto/pizza-napoletana-capricciosa.jpg?s=170667a&w=gi&k=20&c=436lkmvvDZxeIqdYjB6jcHfw2zyeO7dNWLqnc2jxGtc='),(7,'Bufalina', 'Luxurious and creamy with authentic buffalo mozzarella that melts in your mouth.', 10.00, 'https://delivery.ristoranterapsodia.it/wp-content/uploads/2020/04/pizza-bufalina.jpg'),(8,'Marinara', 'Simple yet profound, a traditional Neapolitan pizza that honors the art of minimalism.', 6.50, 'https://theitaliantaste.com/m/cucina-img-webp/900/809-900px.webp'),(9,'Prosciutto e Funghi', 'A classic combination of savory ham and earthy mushrooms beloved across Italy.', 8.70, 'https://i1.wp.com/www.piccolericette.net/piccolericette/wp-content/uploads/2019/10/4102_Pizza.jpg?resize=895%2C616&ssl=1'),(10,'Tonno e Cipolla', 'A Mediterranean treasure featuring tender tuna balanced with sweet caramelized onions.', 8.30, 'https://media-assets.lacucinaitaliana.it/photos/61fbd8df3697f3ffd0cffbc5/16:9/w_2560%2Cc_limit/pizza-tonno-cipolle-800.jpg'),(11,'Salsiccia e Friarielli', 'A Neapolitan specialty pairing rich sausage with slightly bitter broccoli rabe.', 9.50, 'https://www.stuffer.it/wp-content/uploads/2020/12/IMG_9425-scaled.jpg'),(12,'Parmigiana', 'Inspired by the famous eggplant dish, this pizza brings comfort in every slice.', 8.90, 'https://blog.giallozafferano.com/stellaskitchen/wp-content/uploads/2022/01/Eggplant-parmigiana-pizza-scaled.jpg'),(13,'Boscaiola', 'A woodsman-inspired pizza rich with forest mushrooms and creamy indulgence.', 9.80, 'https://www.tasteatlas.com/images/dishes/67a1f8a12ae9474194265ea5217e9b0b.jpg'),(14,'Crudo e Rucola', 'An elegant pizza topped with delicate prosciutto crudo and peppery fresh arugula.', 10.20, 'https://i2.wp.com/www.piccolericette.net/piccolericette/wp-content/uploads/2016/12/3018_Pizza.jpg?resize=895%2C616&ssl=1'),(15,'Tartufo', 'The ultimate luxury pizza featuring aromatic truffle that elevates every bite.', 12.00, 'https://www.truffe-plantin.com/blog/wp-content/uploads/2021/09/summer-truffle-paste-pizza-picture-credit-la-ptite-cuisine-de-pauline-768x509.jpg');
INSERT INTO special_offers (id, offer_first_day, offer_last_day, title, pizza_id) VALUES (1,'2025-10-01','2025-10-31','Weekend Discount',1),(2,'2025-10-10','2025-11-10','Lunch Special',2),(3,'2025-10-15','2025-12-31','Truffle Promo',15),(4,'2025-11-01','2025-11-30','Student Deal',4),(5,'2025-12-15','2026-01-10','Holiday Special',5),(6,'2025-10-20','2025-11-20','Cheesy Combo',6),(7,'2025-10-01','2025-10-31','Weekend Discount',8),(8,'2025-10-01','2025-10-31','Weekend Discount',7),(9,'2025-10-01','2025-10-31','Weekend Discount',13),(10,'2025-10-10','2025-11-10','Lunch Special',9),(11,'2025-10-10','2025-11-10','Lunch Special',10),(12,'2025-10-15','2025-12-31','Truffle Promo',3);
INSERT INTO ingredients (id, name, description) VALUES(1,'Tomato Sauce','Classic tomato sauce made with San Marzano tomatoes. Origin: Campania, Italy. Citation: traditional Italian recipes and regional producers.'),(2,'Mozzarella','Fresh cow''s milk mozzarella used on many pizzas. Origin: Italy. Citation: dairy heritage and PDO traditions.'),(3,'Basil','Aromatic basil leaves (Ocimum basilicum). Commonly used fresh on Margherita. Origin: India (historically) / popularized in Italy. Citation: culinary herb references.'),(4,'Olive Oil','Extra virgin olive oil, cold-pressed. Origin: Mediterranean region (Italy, Greece, Spain). Citation: olive oil producers and culinary sources.'),(5,'Salt','Sea salt or coarse salt, used to season dough and toppings. Origin: coastal evaporation / commercial producers.'),(6,'Pepperoni','American-style spicy cured salami, commonly used on pizzas in the US. Origin: United States (Italian-American adaptation). Citation: food history resources.'),(7,'Oregano','Dried oregano, classic Mediterranean herb often used as a finishing sprinkle. Origin: Mediterranean region.'),(8,'Black Pepper','Ground black pepper; used for seasoning. Origin: Piper nigrum (tropical Asia).'),(9,'Artichoke','Marinated artichoke hearts; used on pizzas like Quattro Stagioni. Origin: Mediterranean agriculture.'),(10,'Ham','Cured ham (cooked or prosciutto-like varieties). Origin: European charcuterie traditions.'),(11,'Mushrooms','Fresh mushrooms, assorted varieties used depending on pizza. Origin: cultivated/foraged sources.'),(12,'Olives','Cured olives (e.g., Kalamata, Gaeta). Origin: Mediterranean groves.'),(13,'Zucchini','Sliced zucchini, often grilled or roasted for vegetarian pizzas. Origin: New World (Americas) but common in Mediterranean cuisines.'),(14,'Bell Pepper','Sweet bell peppers, roasted or fresh. Origin: Americas; widely adopted in global cuisines.'),(15,'Eggplant','Aubergine used in dishes like Parmigiana and on pizzas. Origin: South/East Asia historically; now common in Mediterranean cooking.'),(16,'Cherry Tomato','Small sweet tomatoes used fresh or roasted. Origin: Americas.'),(17,'Spicy Salami','Spicy salami or salame piccante typical in Italian charcuterie. Origin: Italy (regional variations).'),(18,'Chili','Fresh or dried chili peppers used to add heat. Origin: Americas; adopted globally.'),(19,'Buffalo Mozzarella','Mozzarella di bufala campana (made from water buffalo milk). Origin: Campania, Italy. Citation: PDO product history.'),(20,'Garlic','Garlic cloves used fresh or roasted. Origin: Central Asia; used worldwide. Citation: culinary herb references.'),(21,'Prosciutto','Dry-cured ham (prosciutto). Origin: Italy (Parma, San Daniele regions). Citation: charcuterie sources.'),(22,'Parsley','Flat-leaf parsley used as a fresh garnish. Origin: Mediterranean region.'),(23,'Tuna','Canned or preserved tuna used on pizzas like Tonno e Cipolla. Origin: Mediterranean fishing traditions.'),(24,'Onion','Common onion varieties, used raw or caramelized. Origin: Central Asia; widely cultivated.'),(25,'Capers','Pickled caper buds, often from the Capparis plant. Origin: Mediterranean (Sicily, etc.).'),(26,'Sausage','Fresh or cured sausage used as a pizza topping. Origin: European charcuterie traditions.'),(27,'Friarielli','Broccoli rabe (friarielli) typical of Neapolitan cuisine. Origin: Southern Italy.'),(28,'Parmesan','Aged hard cheese (Parmigiano-Reggiano style). Origin: Emilia-Romagna, Italy. Citation: PDO cheese traditions.'),(29,'Cream','Cooking cream used in some specialty pizzas. Origin: dairy processing.'),(30,'Mixed Mushrooms','An assortment of cultivated or wild mushrooms. Origin: varied (cultivated farms or foraged).'),(31,'Prosciutto Crudo','Raw-cured ham, thinly sliced (prosciutto crudo). Origin: Italy. Citation: regional curing methods.'),(32,'Arugula','Peppery salad green (rocket). Origin: Mediterranean region; used fresh as topping.'),(33,'Truffle Cream','Cream infused with truffle flavor; used as a luxurious pizza base. Origin: regions with truffle harvests (Piedmont, Tuscany).'),(34,'Truffle Oil','Infused oil with truffle aroma; used sparingly as a finishing oil. Origin: truffle-producing regions; often culinary product (note: some truffle oils are synthetic aromatized oils).');
